import React, { useState } from 'react';

const classNames = require('classnames');

const Accordion = props => {
    const [ activeIndex, setActiveIndex ] = useState('0');
    const changeActiveIndex = (e) => setActiveIndex(e.target.id);

    return (
        <div>
            <div className="ui divider"></div>
            <div className="ui inverted accordion">
                <div className={classNames({
                    "title" : true,
                    "active" : activeIndex==='0'
                })} id='0' onClick={changeActiveIndex}>
                    <i className="dropdown icon" onClick={changeActiveIndex}></i>
                    {props.title}
                </div>
                <div className={classNames({
                    "content" : true,
                    "active" : activeIndex==='0'
                })} id='0'>
                    <img className="ui large image" alt="" src={props.image}/>
                    <div className="ui divider"></div>
                    <p>{props.body1}</p>
                    <p>{props.body2}</p>
                    <p>{props.body3}</p>
                    <p>{props.body4}</p>
                </div>
            </div>
        </div>
    );
};

export default Accordion;
