import React, { useState } from 'react';
import Section from '../patterns/Section';

const classNames = require('classnames');

const Projects = () => {
    const [ topic, setTopic ] = useState('React');

    const changeTopic = (e) => setTopic(e.target.id);

    return (
        <div>
            <h1>Projects</h1>
            <div className="ui inverted secondary pointing menu">
                <button className={classNames({
                    "item" : true,
                    "active" : topic==="React"
                })} id="React" onClick={changeTopic}>
                    React
                </button>
                <button className={classNames({
                    "item" : true,
                    "active" : topic==="Python"
                })} id="Python" onClick={changeTopic}>
                    Python
                </button>
                <button className={classNames({
                    "item" : true,
                    "active" : topic==="Java"
                })} id="Java" onClick={changeTopic}>
                    Java
                </button>
                <button className={classNames({
                    "item" : true,
                    "active" : topic==="C"
                })} id="C" onClick={changeTopic}>
                    C
                </button>
            </div>
            { topic === "React" && (
                <>
                <Section 
                    title="Powerwash"
                    body1="Currently building a business website for a small power washing company."
                />
                <Section 
                    title="Portfolio"
                    body1="Constantly updating my personal website to keep an accurate record of my projects & accomplishments."
                    body2="This project gave me lots of insight into the world of React development, introducing me to several concepts including reusable components, Router, and useState."
                />
                </>
            )}
            { topic === "Python" && (
                <>
                <Section 
                    title="Fingerprint tool"
                    body1="Script that will run a sequence of commands in order to determine a unique key for that device."
                />
                <Section 
                    title="Face Mask Detector"
                    body1="A library and command line utility for detecting face masks in images and video streams."
                />
                <Section 
                    title="Patient Management System"
                    body1="System to keep track of defibrillator patients billing information."
                />
                <Section 
                    title="Hangman Game"
                    body1="Terminal hangman game with different categories."
                />
                </>
            )}
            { topic === "Java" && (
                <>
                <Section 
                    title="War Game"
                    body1="Object-oriented implementation of the card game War, with three different variations to play. Each game is simulated between two computers."
                    image="/war.png"
                    href="https://www.github.com/kareemsasa3/war-game"
                />
                </>
            )}
            { topic === "C" && (
                <>
                </>
            )}
        </div>
    );
};

export default Projects;
